const webhookData = $('Webhook').item.json;
const body = webhookData.body || webhookData;
const aiResponse = $json;

let carousel = {};
try {
  let text = '';
  if (aiResponse.choices && aiResponse.choices[0] && aiResponse.choices[0].message) {
    const content = aiResponse.choices[0].message.content;
    text = typeof content === 'string' ? content : (content && content[0] && content[0].text ? content[0].text : '');
  } else if (aiResponse.candidates && aiResponse.candidates[0]) {
    text = aiResponse.candidates[0].content.parts[0].text;
  }

  text = (text || '').trim();
  var cb = String.fromCharCode(96);
  if (text.indexOf(cb + cb + cb + 'json') >= 0) text = text.split(cb + cb + cb + 'json').pop();
  if (text.indexOf(cb + cb + cb) >= 0) text = text.split(cb + cb + cb)[0];
  text = text.trim();
  carousel = JSON.parse(text);

  if (carousel.slides && Array.isArray(carousel.slides)) {
    carousel.slides = carousel.slides.map(function(s) {
      var c = s.content;
      if (Array.isArray(c) && c.length > 0 && typeof c[0] === 'object' && c[0].value !== undefined) {
        c = c.map(function(x) { return x.value || x; });
      }
      if (!Array.isArray(c)) c = [];
      return { id: s.id, template: s.template, human_mode: s.human_mode || 'NONE', visual_task: s.visual_task || '', content: c };
    });
  }
} catch (e) {
  carousel = {
    topic: body.topic || '–¢–µ–º–∞',
    product: body.cta || '–ú–ê–ì–ò–Ø',
    post_text: '–õ–∏—Å—Ç–∞–π –∫–∞—Ä—É—Å–µ–ª—å',
    slides: [{
      id: 1,
      template: 'HOOK_PERSON',
      human_mode: 'FACE',
      visual_task: 'Instagram slide. White background. Person in center.',
      content: ['–¢–µ—Å—Ç', '–õ–∏—Å—Ç–∞–π']
    }]
  };
}

return {
  json: {
    chatId: body.chatId,
    topic: carousel.topic || body.topic,
    product: carousel.product || body.cta,
    post_text: carousel.post_text,
    slides: carousel.slides || [],
    totalSlides: (carousel.slides || []).length,
    userPhoto: body.userPhoto,
    stylePrompt: (body.styleConfig && body.styleConfig.style_prompt) || '',
    copywriterSuccess: true
  }
};

const parseData = $('Parse Copywriter').first().json;
const webhookData = $('Webhook').first().json.body;
const slides = parseData.slides || [];

return slides.map(slide => ({
  json: {
    slideId: slide.id,
    template: slide.template,
    humanMode: slide.human_mode,
    visualTask: slide.visual_task,
    content: slide.content,
    userPhoto: webhookData.userPhoto,
    chatId: webhookData.chatId,
    stylePrompt: parseData.stylePrompt || ''
  }
}));

const slides = $('Split Slides').all();
const parseData = $('Parse Copywriter').first().json;
const results = [];

let userPhoto = slides[0]?.json?.userPhoto || '';

if (userPhoto && userPhoto.includes('cloudinary.com')) {
  userPhoto = userPhoto.replace('/upload/', '/upload/f_jpg,q_auto,w_768/');
}

let photoBase64 = null;
if (userPhoto) {
  try {
    const response = await this.helpers.httpRequest({
      method: 'GET',
      url: userPhoto,
      encoding: 'arraybuffer',
      returnFullResponse: true
    });
    photoBase64 = Buffer.from(response.body).toString('base64');
  } catch (e) {
    console.error('Error fetching image:', e.message);
  }
}

// stylePrompt –¥–ª—è –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã—Ö –∏–Ω—Å—Ç—Ä—É–∫—Ü–∏–π
const stylePrompt = slides[0]?.json?.stylePrompt || '';

const faceSuffix = ' [INSTRUCTION: Person must be clearly visible from chest up. Use reference image for character appearance. All visible text on slide must be in Russian language.]';

for (let i = 0; i < slides.length; i++) {
  const slide = slides[i].json;
  const chatId = slide.chatId;

  // visual_task —É–∂–µ —Å–æ–¥–µ—Ä–∂–∏—Ç –æ–ø–∏—Å–∞–Ω–∏–µ —Å—Ç–∏–ª—è –æ—Ç Copywriter
  let visualTask = slide.visualTask;

  // –î–æ–±–∞–≤–ª—è–µ–º –∏–Ω—Å—Ç—Ä—É–∫—Ü–∏—é –ø—Ä–æ —Ä—É—Å—Å–∫–∏–π —Ç–µ–∫—Å—Ç
  visualTask += ' [INSTRUCTION: All text shown on the image must be in Russian/Cyrillic only. Do NOT render any English text on the slide.]';

  const messageContent = [];

  if (slide.humanMode === 'FACE' && photoBase64) {
    messageContent.push({
      type: 'image_url',
      image_url: { url: 'data:image/jpeg;base64,' + photoBase64 }
    });
    messageContent.push({ type: 'text', text: visualTask + faceSuffix });
  } else {
    messageContent.push({ type: 'text', text: visualTask });
  }

  const requestBody = {
    model: 'google/gemini-3-pro-image-preview',
    modalities: ['image', 'text'],
    messages: [{
      role: 'user',
      content: messageContent
    }],
    image_config: {
      aspect_ratio: '4:5',
      image_size: '1K'
    },
    max_tokens: 4096
  };

  results.push({
    json: {
      chatId,
      topic: parseData.topic,
      slideId: slide.slideId,
      template: slide.template,
      totalSlides: parseData.totalSlides,
      requestBody
    }
  });
}

return results;

const geminiItems = $('Gemini Image').all();
const promptItems = $('Prepare Prompt').all();
const results = [];

for (let i = 0; i < geminiItems.length; i++) {
  const response = geminiItems[i].json;
  const prev = promptItems[i].json;

  let imageBase64 = null;

  try {
    if (response.choices && response.choices[0] && response.choices[0].message) {
      const msg = response.choices[0].message;
      if (msg.images && msg.images[0] && msg.images[0].image_url && msg.images[0].image_url.url) {
        const imageUrl = msg.images[0].image_url.url;
        if (imageUrl.startsWith('data:image/')) {
          imageBase64 = imageUrl.replace(/^data:image\/\w+;base64,/, '');
        }
      }
      if (!imageBase64 && Array.isArray(msg.content)) {
        for (const part of msg.content) {
          if (part.image_url && part.image_url.url && part.image_url.url.startsWith('data:image/')) {
            imageBase64 = part.image_url.url.replace(/^data:image\/\w+;base64,/, '');
            break;
          }
        }
      }
    }
    if (!imageBase64 && response.candidates && response.candidates[0]) {
      const parts = response.candidates[0].content.parts;
      for (const part of parts) {
        if (part.inlineData && part.inlineData.data) {
          imageBase64 = part.inlineData.data;
          break;
        }
      }
    }
  } catch (e) {
    console.error('Error extracting image:', e.message);
  }

  if (imageBase64) {
    results.push({
      json: {
        chatId: prev.chatId,
        topic: prev.topic,
        slideId: prev.slideId,
        template: prev.template,
        totalSlides: prev.totalSlides,
        imageBase64,
        hasImage: true
      }
    });
  }
}

return results;

const cloudinaryItems = $('Cloudinary Upload').all();
const extractItems = $('Extract Image').all();

const imageUrls = [];
const publicIds = [];
let chatId = null;
let topic = null;

for (let i = 0; i < cloudinaryItems.length; i++) {
  const cloudinary = cloudinaryItems[i].json;
  const extract = extractItems[i].json;

  if (!chatId) chatId = extract.chatId;
  if (!topic) topic = extract.topic;

  if (cloudinary.secure_url) {
    imageUrls.push(cloudinary.secure_url);
  }
  if (cloudinary.public_id) {
    publicIds.push(cloudinary.public_id);
  }
}

return [{
  json: {
    chatId,
    topic,
    imageUrls,
    publicIds,
    totalSlides: imageUrls.length
  }
}];

const collectData = $('Collect URLs').first().json;
const publicIds = collectData.publicIds || [];

if (publicIds.length === 0) {
  return [{ json: { deleted: 0, message: 'No images to delete' } }];
}

try {
  const response = await this.helpers.httpRequest({
    method: 'DELETE',
    url: 'https://api.cloudinary.com/v1_1/ds8ylsl2x/resources/image/upload',
    auth: {
      username: '329318552456676',
      password: '1W-H9NYn1vOxwshd7S6iug3JcWk'
    },
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    },
    body: 'prefix=user_carousels/'
  });

  const deletedCount = response.deleted ? Object.keys(response.deleted).length : 0;

  return [{
    json: {
      deleted: deletedCount,
      response
    }
  }];
} catch (error) {
  return [{
    json: {
      deleted: 0,
      error: error.message,
      publicIds
    }
  }];
}
